<?phpclass Welcome extends CI_Controller{	function index()	{		if(!$this->ion_auth->logged_in())		{			redirect('auth','refresh');		}		elseif($this->ion_auth->logged_in())		{			$data['user'] = $this->ion_auth->user()->row();			$data['post'] = $this->welcome_model->get();			$data['container'] = $this->load->view('container');			$this->load->view('welcome_view', $data);		}	}		function do_upload()	{		$user = $this->ion_auth->user()->row();		$config['upload_path'] = './assets/pics/';		$config['allowed_types'] = 'jpg';		$config['file_name'] = $user->first_name;		$config['overwrite'] = TRUE;		$this->load->library('upload', $config);		if ( ! $this->upload->do_upload())		{			redirect('welcome','refresh');		}		else		{			redirect('welcome','refresh');		}	}		function post_insert()		{		$this->welcome_model->insert();		redirect('welcome','refresh');	}}