<?phpclass Welcome extends CI_Controller{    public function __construct()    {      parent::__construct();	  $this->load->library('ion_auth');}	function index()	{	if (!$this->ion_auth->logged_in())		{		redirect('auth','refresh');		}elseif($this->ion_auth->logged_in()){		$this->load->library('ion_auth');	$this->load->model('welcome_model');	$data['user'] = $this->ion_auth->user()->row();	$data['post'] = $this->welcome_model->get();	$data['container'] = $this->load->view('container');	$this->load->view('welcome_view', $data);	}	}		function do_upload()	{		$user = $this->ion_auth->user()->row();		$config['upload_path'] = './assets/pics/';		$config['allowed_types'] = 'gif|jpg|png';		$config['file_name'] = $user->first_name;		$config['overwrite'] = TRUE;		$this->load->library('upload', $config);		if ( ! $this->upload->do_upload())		{			redirect('welcome','refresh');		}		else		{		redirect('welcome','refresh');		}	}		function post_insert()	{		$this->load->model('welcome_model');		$this->welcome_model->insert();		redirect('welcome','refresh');}	}